package com.test;

public class Short {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		short b;
		short max;
		short min;
		for(b=0,max=(short)(b-1);b>max;b++);
		System.out.println(b);
		
		for(b=0,min=(short)(b+1);b<min;b--);
		System.out.println(b);
	}

}
